class Player:
    def __init__(self, name):
        """Initialize player object and player attributes"""
        self.name = name
        self.rounds_played = 0
        self.rounds_won = 0 
        self.accuracy_rate = 0
        self.funds = float(5000)      
        self.correct = 0      #number of questions answered correctly in a round
        self.cumulative_correct=0 #cumulative number of questions answered correctly across all rounds
     
    def set_correct_zero(self):
        """Reset correct answer count per round to zero after the end of each round"""
        self.correct=0
        
    def set_cumulative_correct(self):
        """Track the cumulative number of correct answers across all rounds played"""
        self.cumulative_correct+=self.correct
    
    def get_cumulative_correct(self):
        """Report the cumulative number of correct answers across all rounds played"""
        return self.cumulative_correct
        
    def get_name(self):
        """get name of player"""
        return self.name
    
    def get_rounds_played(self):
        """get number of rounds player has played"""
        return self.rounds_played
    
    def set_rounds_played(self):
        """update number of rounds played at the end of each round"""
        self.rounds_played = self.rounds_played + 1
    
    def get_rounds_won(self):  
        """get number of rounds that player has won"""
        return self.rounds_won
    
    def update_rounds_won(self):
        """update number of rounds that player has won"""
        self.rounds_won = self.rounds_won + 1
    
    def get_correct(self):
        """get number of correct answers"""
        return self.correct
    
    def update_correct(self):
        """update number of correct answers"""
        self.correct = self.correct + 1
    
    def get_accuracy_rate(self):
        """Calculate and get player's accuracy rate"""
        total_qs=self.rounds_played*5
        self.accuracy_rate=self.cumulative_correct/total_qs
        return self.accuracy_rate       

    def get_funds_available(self):
        """return funds available"""
        return self.funds
    
    def incr_funds_available(self, increase):
        """increase funds available after answering question correctly"""
        self.funds = self.funds + increase
        
    def decr_funds_available(self, decrease):   
        """decrease funds available after answering question incorrectly
        or after investing funds in hospital"""
        self.funds = self.funds -decrease
        
    def select_opponent(self, Opponent):
        """player can select one of three opponents available for the game"""
        while True:
            opp = input("Please chooose from the following three opponents: enter 'A' for Professor Brainiac, 'B' for Physician Heartfilia, or 'C' for Resident Sunshine")
            opp = opp.upper()
            if (opp == "A"):
                Opponent.pick_opponent("Professor Brainiac")
                print("\n" +'Professor Brainiac is ready to challenge you!')
                print("\n" +'Hello, young one! You are brave to be challenging the unbeaten champion of Medical Jeopardy! Do not let me down!')
                break
                
            elif (opp == "B"):
                Opponent.pick_opponent("Physician Heartfilia")
                print("\n" +'Curious one are not afraid?')
                print(' I will take you up on your challenge! See you in the ring.')
                break
                
            elif (opp == "C"):
                Opponent.pick_opponent("Resident Sunshine")
                print("\n" + 'Hi there! I love seeing patients and making my rounds.')
                print('Let us see who is better!')
                break
     


class Opponent:
    def __init__ (self):
        """initialize opponent object and attributes"""
        self.name = ""
        self.answer_accuracy = 0
        self.tracker_for_medal_award=0
        self.num_rounds_won = 0
        self.num_rounds_lost = 0
        self.correct = 0       # number of correct answers by opponent per round
        self.wait_time = 0
    
    def set_tracker_for_medal_award(self):
        """track to see if opponent has lost 3 times to player and if so reset the count"""
        if self.num_rounds_lost<=3:
            self.tracker_for_medal_award=self.num_rounds_lost
        else:
            self.tracker_for_medal_award=1
        
    def get_tracker_for_medal_award(self):
        """report tracker of times opponent lost for separate function to decide whether to award medal"""
        return self.tracker_for_medal_award
    
    def set_accuracy(self):
        """Set answer accuracy rate for opponent chosen by player"""
        if self.name=="Professor Brainiac":
            self.answer_accuracy=0.7
        elif self.name=="Physician Heartfilia":
            self.answer_accuracy=0.5
        elif self.name=="Resident Sunshine":
            self.answer_accuracy=0.3

    def set_wait_time(self):
        """Set opponent wait time before answering question"""
        if self.name=="Professor Brainiac":
            self.wait_time=12
        elif self.name=="Physician Heartfilia":
            self.wait_time=16
        elif self.name=="Resident Sunshine":
            self.wait_time=20
    
    
    def get_name(self):
        """Set opponent name"""
        return self.name
            
    def get_wait_time(self):
        """Obtain wait time of opponent"""
        return self.wait_time
    
    def dec_wait_time(self):
        """Decrease wait time of opponents before answering question"""
        if self.wait_time > 5: 
            self.wait_time = self.wait_time - 1.5
    
    def set_correct_zero(self):
        """Set number of correct opponent answers to zero"""
        self.correct=0
    
    def get_correct(self):
        """get number of questions opponent has answered correctly per round"""
        return self.correct
    
    def update_correct(self):
        """update number of questions opponent has answered
        correctly after each question"""
        self.correct = self.correct + 1
            
    def pick_opponent(self, name):
        """player selects an opponent based on a pre-determined
        list of 3 names"""
        self.name = name
    
    def get_answer_accuracy(self):
        """get percentage of accurate answer opponent will 
        answer correctly based on"""
        return self.answer_accuracy
        
    def incr_answer_accuracy(self):
        """After every round, opponent answer accuracy increases by 0.15. 
        Maximum opponent accuracy is 0.85"""
        if self.answer_accuracy < 0.85:
            self.answer_accuracy = self.answer_accuracy + 0.15
        
    def set_num_rounds_won (self):
        """When computer wins the round,
        the Game calls this method to update the opponent num_rounds_won"""
        self.num_rounds_won = self.num_rounds_won + 1
            
    def set_num_rounds_lost (self):       
        """when computer loses the round, 
        the Game calls this method to update the opponent num_rounds_lost"""
        self.num_rounds_lost = self.num_rounds_lost + 1
    
    def get_num_rounds_won(self):
        """get number of round opponent won"""
        return self.num_rounds_won
    
    def get_num_rounds_lost(self):
        """get number of round opponent lost"""
        return self.num_rounds_lost



import random as rand

import time

class Game:
    
    def __init__(self):
        """This initializes an object that presents questions and has methods to grade answers"""
        #value of question
        self.quest_value = float(0)
    
    
    def round(self, Player, Opponent):
        """Reports status of funds, question value 
        and opponent stats at start of each round and updates values"""
        self.quest_value = self.quest_value + 2000
        Opponent.dec_wait_time()
        Opponent.incr_answer_accuracy()
        print("\n"+"Welcome to a new round. " + "\n")
        time.sleep(1)
        print("Your current funds are: " + "${:,.2f}".format(Player.get_funds_available()) + "\n")
        time.sleep(1)
        print("Questions in this round are worth:  " + "${:,.2f}".format((self.quest_value)) + "\n")
        time.sleep(1)
        print("You will have " + str(Opponent.get_wait_time()) + " seconds before your opponent answers" + "\n")
        
        
    def present_question(self, Player, Opponent):
        """This presents one question and verifies player's answer for that question"""
        """Randomly picks a question""" 
        rand_question = rand.randint(0,150)
        """Randomly picks position for the answer choice to be located"""
        rand_question_pos = rand.randint(0,3)
        print(questions[rand_question])
        #holds the four answer choices
        choices = [0,0,0,0]      
        #holds the correct answer among the four choices
        choices[rand_question_pos] = answers[rand_question]
        
        #Randomly assign wrong answers to the other three choices
        q=0
        while (0 in choices):
            c = rand.randint(0,150) 
            if (answers[c] not in choices):
                
                if (q == rand_question_pos):
                    q = q+1
                    choices[q] = answers[c]
                    
                else:
                    choices[q] = answers[c]
           
                q= q+1
                       
        print("\n")
        print("(1) for " + choices[0] + "\n")
        print("(2) for " + choices[1] + "\n")
        print("(3) for " + choices[2] + "\n")
        print("(4) for " + choices[3] + "\n")
        
        #Code for answering question and calculating time taken by player to answer question
        start_time = time.time()
        
        player_ans = input("Please select an answer:  " + "\n")
        
        lapsed_time = time.time() - start_time
        
        #How long before computer answers
        comp_wait_time = Opponent.get_wait_time()
        
        #Correct choice is (rand_question_pos plus 1) if using (1,2,3,4) instead of (0,1,2,3)
        correct_choice =  str(rand_question_pos + 1)
        
        #If player is faster than computer opponent
        if lapsed_time < comp_wait_time: 
            
            if player_ans == correct_choice:
                print("You are correct!")
                print("\n"+ "The right answer is : " + answers[rand_question])
                
                Player.incr_funds_available(self.quest_value)  
                Player.update_correct()
                
                
            if player_ans != correct_choice:
                print("You are incorrect!")
                print("\n"+ "The right answer is : " + answers[rand_question])
                
                Player.decr_funds_available(self.quest_value)
        
        #If player is slower than computer opponent
        if lapsed_time > comp_wait_time:
            
            #Use accuracy of opponent to see if they get right answer
            opp_accuracy = Opponent.get_answer_accuracy()
            
            #If opponent answers correctly
            if rand.random() < opp_accuracy:
                
                print("Sorry you were too slow and your opponent answered it correctly first.")
                print("\n"+ "The right answer is : " + answers[rand_question])
                Opponent.update_correct()
            
            else:
            #If opponent answers incorrectly and player answers correctly
                if player_ans == correct_choice:
                    print("Sorry you were too slow and your opponent answered it first. Lucky for you they were wrong and your answer is correct!")
                    print("\n"+ "The right answer is : " + answers[rand_question])
                    Player.incr_funds_available(self.quest_value)  
                    Player.update_correct()
            #If opponent answers incorrectly and player answer incorrectly 
                if player_ans != correct_choice:
                    print("Sorry you were too slow and your opponent answered it first incorrectly. Unfortunately your answer is incorrect too!")
                    print("\n"+ "The right answer is : " + answers[rand_question])
                    Player.decr_funds_available(self.quest_value)
                
        


