class Hospital:
    
    def __init__(self, name):
        """Initialize a hospital object with name, hospital grade, medals and black mark records"""
        self.name = name
        self.grade = "C"
        self.medals = 0
        self.spare_medal=0
        self.black_marks = 0
     
    def get_name(self):
        """Return hospital name"""
        return self.name
    
    def get_grade(self):
        """Report hospital grade"""
        return self.grade
    
    def upgrade(self):
        """Upgrade hospital"""
        score = grades.index(self.grade)
        # If the hospital grade is A then it can't go higher
        if (score > 0):
            self.grade = grades[score-1]
        
    def downgrade(self):
        """Downgrade hospital"""
        score = grades.index(self.grade)
        # If the grade is F then it can't go lower
        self.grade = grades[score+1]
    
    def get_black_marks(self):
        #Provide number of hospital black marks
        return self.black_marks
    
    def award_black_marks(self):
        
        if self.black_marks == 2:
            
            self.black_marks = 0
            print("Sorry you have acquired three black marks and now your hospital"+str(self.name)+" gets downgraded from " + self.grade )
            self.downgrade()
            print("to " + self.grade)
        
        else:
            self.black_marks = self.black_marks + 1
            print("Your hospital "+str(self.name)+" has acquired another black mark and now has " + str(self.black_marks)+" black marks.")
    
    def get_spare_medal(self):
        #report unused medals
        return self.spare_medal
    
    def use_spare_medal(self):
        #action to use a spare medal when needed
        self.spare_medal-=1
    
    def award_medals(self,Player,Opponent):
        #award medals when player has 3 wins over opponent
        self.medals = self.medals + 1
        medal_type=""
        if Opponent.get_name()=="Professor Brainiac":
            medal_type="Golden Brain"
        elif Opponent.get_name()=="Physician Heartfilia":
            medal_type="Silver Heart"
        elif Opponent.get_name()=="Resident Sunshine":
            medal_type="Shining Soul"
        print(str(Opponent.get_name())+" says ,'Well played "+str(Player.get_name())+"!'.")
        #If grade is already A then store the medal as a spare, otherwise use it.
        if self.grade=='A':
            print("You have earned a "+medal_type+" medal for your hospital "+self.name+". However, your hospital is already at the maximum grade of A." )
            self.spare_medal+=1
        else:
            print("You have earned a "+medal_type+" medal for your hospital and as a bonus the hospital "+self.name+" gets upgraded from " + self.grade)
            self.upgrade()
            print("to " + self.grade)
        


